mapper.select:
select

from sys_dict
where 1=1

  and id=#{id,jdbcType=INTEGER}


  and `type` like CONCAT('%', #{type,jdbcType=VARCHAR}, '%')


  and type_name like CONCAT('%', #{typeName,jdbcType=VARCHAR}, '%')


  and `label`=#{label,jdbcType=VARCHAR}


  and `value`=#{value,jdbcType=INTEGER}


  and remark like CONCAT('%', #{remark,jdbcType=VARCHAR}, '%')


  and seq=#{seq,jdbcType=INTEGER}

order by create_time desc, seq desc
