mapper.select:
select resource.id, resource.parent_id, resource.`type`, resource.`name`, resource.uri, resource.code, resource.icon, resource.remark, resource.seq, resource.client_type, resource.`enable`, x.api_id apiId from sys_resource resource left join sys_resource_api x on resource.id = x.resource_id where del = false
and resource.client_type=#{clientType,jdbcType=TINYINT}
and resource.`enable`=#{enable,jdbcType=BOOLEAN}
order by seq desc

select id, parent_id parentId, `type`, `name`, uri, code, icon from sys_resource resource where del = false and enable = true
and client_type=#{clientType,jdbcType=TINYINT}
and type=#{type,jdbcType=TINYINT}
order by parent_id, seq desc
